mapper.update:
update user
username = #{username}, password = #{password}, validate = #{validate}, user_logo = #{userLogo}, status = #{status}, deleting = #{deleting}, create_time = #{createTime}, last_login_time = #{lastLoginTime}, update_user = #{updateUser}, update_time = #{updateTime},
where id = #{id}

mapper.select:
select * from user

    validate > 0
    and username like concat('%',#{username},'%')
    and status = #{status}
    and deleting = #{deleting}
    and update_user like concat('%',#{updateUser},'%')
    and validate = #{validate}
